package com.example.submeapp;

import android.os.Bundle;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentTransaction;

import com.google.android.material.bottomnavigation.BottomNavigationView;

public class MainActivity extends AppCompatActivity {

    private BottomNavigationView bottomNavigationView;
    private FragmentManager fragmentManager;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_main);
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        bottomNavigationView = findViewById(R.id.bottomNavigation);
        fragmentManager = getSupportFragmentManager();

        // Load default fragment (Home)
        loadFragment(new HomeFragment());

        // Setup bottom navigation listener
        bottomNavigationView.setOnItemSelectedListener(item -> {
            Fragment fragment = null;
            int itemId = item.getItemId();

            if (itemId == R.id.nav_home) {
                fragment = new HomeFragment();
            } else if (itemId == R.id.nav_list) {
                fragment = new ListFragment();
            } else if (itemId == R.id.nav_notifications) {
                fragment = new NotificationsFragment();
            } else if (itemId == R.id.nav_profile) {
                fragment = new ProfileFragment();
            }

            if (fragment != null) {
                loadFragment(fragment);
                return true;
            }
            return false;
        });
    }

    private void loadFragment(Fragment fragment) {
        FragmentTransaction transaction = fragmentManager.beginTransaction();
        transaction.replace(R.id.fragmentContainer, fragment);
        transaction.commit();
    }
}

package com.example.submeapp;

import android.os.Bundle;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentManager;
import androidx.fragment.app.FragmentTransaction;

import com.google.android.material.bottomnavigation.BottomNavigationView;

public class MainActivity extends AppCompatActivity {

    private BottomNavigationView bottomNavigationView;
    private FragmentManager fragmentManager;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        EdgeToEdge.enable(this);
        setContentView(R.layout.activity_main);
        ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
            Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
            v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
            return insets;
        });

        bottomNavigationView = findViewById(R.id.bottomNavigation);
        fragmentManager = getSupportFragmentManager();

        // Load default fragment (Home)
        loadFragment(new HomeFragment());

        // Setup bottom navigation listener
        bottomNavigationView.setOnItemSelectedListener(item -> {
            Fragment fragment = null;
            int itemId = item.getItemId();

            if (itemId == R.id.nav_home) {
                fragment = new HomeFragment();
            } else if (itemId == R.id.nav_list) {
                fragment = new ListFragment();
            } else if (itemId == R.id.nav_notifications) {
                fragment = new NotificationsFragment();
            } else if (itemId == R.id.nav_profile) {
                fragment = new ProfileFragment();
            }

            if (fragment != null) {
                loadFragment(fragment);
                return true;
            }
            return false;
        });
    }

    private void loadFragment(Fragment fragment) {
        FragmentTransaction transaction = fragmentManager.beginTransaction();
        transaction.replace(R.id.fragmentContainer, fragment);
        transaction.commit();
    }

    // Helper methods for navigation from fragments
    public void navigateToNotifications() {
        bottomNavigationView.setSelectedItemId(R.id.nav_notifications);
    }

    public void navigateToList() {
        bottomNavigationView.setSelectedItemId(R.id.nav_list);
    }

    public void navigateToProfile() {
        bottomNavigationView.setSelectedItemId(R.id.nav_profile);
    }

    public void navigateToHome() {
        bottomNavigationView.setSelectedItemId(R.id.nav_home);
    }
}

package com.example.submeapp;

import android.content.Intent;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;
import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;
import com.bumptech.glide.Glide;
import com.example.submeapp.api.models.Plan;
import java.text.NumberFormat;
import java.util.List;
import java.util.Locale;

public class PlanAdapter extends RecyclerView.Adapter<PlanAdapter.PlanViewHolder> {

    private List<Plan> planList;

    public PlanAdapter(List<Plan> planList) {
        this.planList = planList;
    }

    @NonNull
    @Override
    public PlanViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
        View view = LayoutInflater.from(parent.getContext())
                .inflate(R.layout.item_product, parent, false);
        return new PlanViewHolder(view);
    }

    @Override
    public void onBindViewHolder(@NonNull PlanViewHolder holder, int position) {
        Plan plan = planList.get(position);

        holder.productTitle.setText(plan.getName());

        // Format price
        try {
            double price = Double.parseDouble(plan.getPrice());
            NumberFormat formatter = NumberFormat.getInstance(new Locale("vi", "VN"));
            holder.productPrice.setText(formatter.format(price) + "đ/" + plan.getDurationUnit());
        } catch (Exception e) {
            holder.productPrice.setText(plan.getPrice() + "đ");
        }

        // Load image if available
        if (plan.getImageUrl() != null && !plan.getImageUrl().isEmpty()) {
            Glide.with(holder.itemView.getContext())
                    .load(plan.getImageUrl())
                    .placeholder(R.drawable.ic_package)
                    .error(R.drawable.ic_package)
                    .into(holder.productImage);
        }

        // Click on card to view details
        holder.itemView.setOnClickListener(v -> {
            Intent intent = new Intent(v.getContext(), ProductDetailActivity.class);
            intent.putExtra("planId", plan.getId());
            v.getContext().startActivity(intent);
        });

        // Click on "Xem chi tiết" button
        holder.btnViewDetail.setOnClickListener(v -> {
            Intent intent = new Intent(v.getContext(), ProductDetailActivity.class);
            intent.putExtra("planId", plan.getId());
            v.getContext().startActivity(intent);
        });
    }

    @Override
    public int getItemCount() {
        return planList != null ? planList.size() : 0;
    }

    // Method to update list for filtering
    public void updateList(List<Plan> newList) {
        this.planList = newList;
        notifyDataSetChanged();
    }

    static class PlanViewHolder extends RecyclerView.ViewHolder {
        TextView productTitle, productPrice;
        ImageView productImage;
        android.widget.Button btnViewDetail;

        public PlanViewHolder(@NonNull View itemView) {
            super(itemView);
            productTitle = itemView.findViewById(R.id.productTitle);
            productPrice = itemView.findViewById(R.id.productPrice);
            productImage = itemView.findViewById(R.id.productImage);
            btnViewDetail = itemView.findViewById(R.id.btnAddToCart);
        }
    }
}

package com.example.submeapp.api.models;

import com.google.gson.annotations.SerializedName;

public class Plan {
    private int id;

    @SerializedName("vendor_id")
    private int vendorId;

    @SerializedName("category_id")
    private int categoryId;

    private String name;
    private String description;
    private String price;

    @SerializedName("duration_unit")
    private String durationUnit;

    @SerializedName("duration_value")
    private int durationValue;

    private String status;

    @SerializedName("createdAt")
    private String createdAt;

    // Getters and Setters
    public int getId() {
        return id;
    }

    public void setId(int id) {
        this.id = id;
    }

    public int getVendorId() {
        return vendorId;
    }

    public void setVendorId(int vendorId) {
        this.vendorId = vendorId;
    }

    public int getCategoryId() {
        return categoryId;
    }

    public void setCategoryId(int categoryId) {
        this.categoryId = categoryId;
    }

    public String getName() {
        return name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public String getDescription() {
        return description;
    }

    public void setDescription(String description) {
        this.description = description;
    }

    public String getPrice() {
        return price;
    }

    public void setPrice(String price) {
        this.price = price;
    }

    public String getDurationUnit() {
        return durationUnit;
    }

    public void setDurationUnit(String durationUnit) {
        this.durationUnit = durationUnit;
    }

    public int getDurationValue() {
        return durationValue;
    }

    public void setDurationValue(int durationValue) {
        this.durationValue = durationValue;
    }

    public String getStatus() {
        return status;
    }

    public void setStatus(String status) {
        this.status = status;
    }

    public String getCreatedAt() {
        return createdAt;
    }

    public void setCreatedAt(String createdAt) {
        this.createdAt = createdAt;
    }

    // Helper method to format price
    public String getFormattedPrice() {
        try {
            double priceValue = Double.parseDouble(price);
            return String.format("%,.0f₫", priceValue);
        } catch (NumberFormatException e) {
            return price + "₫";
        }
    }

    // Helper method to get duration text
    public String getDurationText() {
        return durationValue + " " + durationUnit;
    }

    // Helper method to check if approved
    public boolean isApproved() {
        return "approved".equalsIgnoreCase(status);
    }
}

